pages.content:
/* Registering Service Worker */
if('serviceWorker' in navigator) {
  navigator.serviceWorker.register('/sw.js');
};

{% capture source %}
<feed xmlns="http://www.w3.org/2005/Atom">
  <id>{{ "/" | absolute_url }}</id>
  <title>{{ site.title }}</title>
  <subtitle>{{ site.description }}</subtitle>
  <updated>{{ site.time | date_to_xmlschema }}</updated>
  <author>
    <name>{{ site.social.name }}</name>
    <uri>{{ "/" | absolute_url }}</uri>
  </author>
  <link rel="self" type="application/atom+xml" href="{{ page.url | absolute_url }}"/>
  <link rel="alternate" type="text/html" hreflang="{{ site.lang }}"
    href="{{ '/' | absolute_url }}"/>
  <generator uri="https://jekyllrb.com/" version="{{ jekyll.version }}">Jekyll</generator>
  <rights> ¬© {{ 'now' | date: '%Y' }} {{ site.social.name }} </rights>
  <icon>{{ site.baseurl }}/assets/img/favicons/favicon.ico</icon>
  <logo>{{ site.baseurl }}/assets/img/favicons/favicon-96x96.png</logo>

{% for post in site.posts limit: 5 %}
  {% assign post_absolute_url = post.url | absolute_url %}
  <entry>
    <title>{{ post.title }}</title>
    <link href="{{ post_absolute_url }}" rel="alternate" type="text/html" title="{{ post.title }}" />
    <published>{{ post.date | date_to_xmlschema }}</published>
  {% if post.last_modified_at %}
    <updated>{{ post.last_modified_at | date_to_xmlschema }}</updated>
  {% else %}
    <updated>{{ post.date | date_to_xmlschema }}</updated>
  {% endif %}
    <id>{{ post_absolute_url }}</id>
    <content src="{{ post_absolute_url }}" />
    <author>
      <name>{{ post.author | default: site.social.name }}</name>
    </author>

  {% if post.categories %}
    {% for category in post.categories %}
    <category term="{{ category }}" />
    {% endfor %}
  {% endif %}

  {% if post.summary %}
    <summary>{{ post.summary | strip }}</summary>
  {% else %}
    <summary>
      {% include no-linenos.html content=post.content %}
      {{ content  | strip_html | truncate: 400 }}
    </summary>
  {% endif %}

  </entry>
{% endfor %}
</feed>
{% endcapture %}
{{ source | replace: '&', '&amp;' }}

[
  {% for post in site.posts %}
  {
    "title": "{{ post.title | escape }}",
    "url": "{{ post.url | relative_url }}",
    "categories": "{{ post.categories | join: ', '}}",
    "tags": "{{ post.tags | join: ', ' }}",
    "date": "{{ post.date }}",
    {% include no-linenos.html content=post.content %}
    "snippet": "{{ content | strip_html | strip_newlines | remove_chars | escape | replace: '\', '\\\\' }}"
  }{% unless forloop.last %},{% endunless %}
  {% endfor %}
]

{% assign favicon_path = "/assets/img/favicons" | relative_url %}

{
  "name": "{{ site.title }}",
  "short_name": "{{ site.title }}",
  "description": "{{ site.description }}",
  "icons": [
    {
      "src": "{{ favicon_path }}/android-chrome-192x192.png",
      "sizes": "192x192",
      "type": "image/png"
    },
    {
      "src": "{{ favicon_path }}/android-chrome-512x512.png",
      "sizes": "512x512",
      "type": "image/png"
    }],
  "start_url": "{{ '/index.html' | relative_url }}",
  "theme_color": "#2a1e6b",
  "background_color": "#ffffff",
  "display": "fullscreen"
}

/*
  If the number of TAB files has changed, the following variable is required.
  And it must be defined before `@import`.
*/
$tab-count: {{ site.tabs | size | plus: 1 }}; // plus 1 for home tab

@import "{{ site.theme }}";

/* append your custom style below */

self.importScripts('{{ "/assets/js/data/swcache.js" | relative_url }}');

const cacheName = 'chirpy-{{ "now" | date: "%Y%m%d.%H%M" }}';

function verifyDomain(url) {
  for (const domain of allowedDomains) {
    const regex = RegExp(`^http(s)?:\/\/${domain}\/`);
    if (regex.test(url)) {
      return true;
    }
  }

  return false;
}

function isExcluded(url) {
  for (const item of denyUrls) {
    if (url === item) {
      return true;
    }
  }
  return false;
}

self.addEventListener('install', e => {
  self.skipWaiting();
  e.waitUntil(
    caches.open(cacheName).then(cache => {
      return cache.addAll(resource);
    })
  );
});

self.addEventListener('fetch', event => {
  event.respondWith(
    caches.match(event.request)
      .then(response => {
        if (response) {
          return response;
        }

        return fetch(event.request)
          .then(response => {
            const url = event.request.url;

            if (event.request.method !== 'GET' ||
              !verifyDomain(url) ||
              isExcluded(url)) {
              return response;
            }

            /*
              see: <https://developers.google.com/web/fundamentals/primers/service-workers#cache_and_return_requests>
             */
            let responseToCache = response.clone();

            caches.open(cacheName)
              .then(cache => {
                /* console.log('[sw] Caching new resource: ' + event.request.url); */
                cache.put(event.request, responseToCache);
              });

            return response;
          });
      })
    );
});

self.addEventListener('activate', e => {
  e.waitUntil(
    caches.keys().then(keyList => {
          return Promise.all(
            keyList.map(key => {
              if(key !== cacheName) {
                return caches.delete(key);
              }
            })
          );
    })
  );
});

const resource = [

  /* --- CSS --- */
  '{{ "/assets/css/style.css" | relative_url }}',

  /* --- PWA --- */
  '{{ "/app.js" | relative_url }}',
  '{{ "/sw.js" | relative_url }}',

  /* --- HTML --- */
  '{{ "/index.html" | relative_url }}',
  '{{ "/404.html" | relative_url }}',
  {% for tab in site.tabs %}
    '{{ tab.url | relative_url }}',
  {% endfor %}

  /* --- Favicons & compressed JS --- */
  {% assign cache_list = site.static_files | where: 'swcache', true  %}
  {% for file in cache_list %}
    '{{ file.path | relative_url }}'{%- unless forloop.last -%},{%- endunless -%}
  {% endfor %}

];

/* The request url with below domain will be cached */
const allowedDomains = [
  {% if site.google_analytics.id != empty and site.google_analytics.id %}
    'www.googletagmanager.com',
    'www.google-analytics.com',
  {% endif %}

  '{{ site.url | split: "//" | last }}',

  {% if site.img_cdn contains '//' and site.img_cdn %}
    '{{ site.img_cdn | split: '//' | last | split: '/' | first }}',
  {% endif %}

  'fonts.gstatic.com',
  'fonts.googleapis.com',
  'cdn.jsdelivr.net',
  'polyfill.io'
];

/* Requests that include the following path will be banned */
const denyUrls = [
  {% if site.google_analytics.pv.cache_path %}
    '{{ site.google_analytics.pv.cache_path | absolute_url }}'
  {% endif %}
];
